pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/IERC721Enumerable.sol";

/**
 * @notice Operations only for Army contract
 */
interface ARMY is IERC721Enumerable {
    function minionExists(uint) external view returns (bool);
    function getMinionInfo(uint) external view returns (address, bool, int, int);
    function updateBaseURI(string memory) external;
}

/**
 * @title Battle mechanism
 * @notice Define how to figh on battlefield
 * @author Justa Liang
 */
abstract contract BattleBase is Ownable {

    /// @notice If Battlefield locked, lock for vote, unlock for proposal
    bool public fieldLocked;

    /// @notice Total area of battlefield
    uint public totalArea;

    /// @notice Defenders on cetain field
    mapping (uint => uint[]) public fieldDefender;

    /// @notice If field be occupied by flora army 
    mapping (uint => bool) public isFloraField;

    /// @notice If certain flora minion on field
    mapping (uint => bool) public floraOnField;

    /// @notice If certain fauna minion on field
    mapping (uint => bool) public faunaOnField;

    /// @notice Corresponding FloraArmy contract
    ARMY public floraArmy;

    /// @notice Corresponding FaunaArmy contract
    ARMY public faunaArmy;

    /// @notice Number of fields occupied by flora army
    uint public floraFieldCount;

    /// @notice Number of fields occupied by fauna army
    uint public faunaFieldCount;

    /// @dev Initial power of flora and fauna minions
    int private _refPower;

    /// @dev Field info for frontend
    struct FieldInfo {
        address leader;
        uint[] defender;
        bool isFlora;
    }

    /// @notice Emit when field's state changes
    event FieldState(uint indexed fieldID,
                     address indexed conqueror,
                     bool indexed isGreen,
                     uint[] defender);

    /// @notice Emit when total area of battlefield changes
    event TotalArea(uint totalArea);

    /**
     * @dev Set addresses of interactive contracts
     * @param floraArmyAddr Address of FloraArmy contract
     * @param faunaArmyAddr Address of FaunaArmy contract
    */
    constructor(address floraArmyAddr, address faunaArmyAddr) {
        fieldLocked = false;
        totalArea = 20;
        _refPower = 1000;
        floraArmy = ARMY(floraArmyAddr);
        faunaArmy = ARMY(faunaArmyAddr);
        floraFieldCount = 0;
        faunaFieldCount = 0;

        emit TotalArea(totalArea);
    }

    /**
     * @notice Expand the battlefield and increase it's total area
    */
    function expand(uint increaseSize) external onlyOwner {
        totalArea += increaseSize;
        require(
            totalArea < floraArmy.totalSupply() + faunaArmy.totalSupply(),
            "Battlefield: no need for expansion");

        emit TotalArea(totalArea);
    }

    /**
     * @notice Get minion IDs on certain field
     * @param fieldID ID of the field
     * @return Array of minion IDs
    */ 
    function getFieldDefender(uint fieldID) public view returns (uint[] memory) {
        return fieldDefender[fieldID];
    }

    /**
     * @notice Get the leader on certain field
     * @param fieldID ID of the field
     * @return Owner of the first minion
    */ 
    function getFieldLeader(uint fieldID) public view returns (address) {
        uint[] memory defender = fieldDefender[fieldID];
        if (defender.length == 0) {
            return address(0);
        }
        else if (isFloraField[fieldID]) {
            if (floraArmy.minionExists(defender[0])) {
                return floraArmy.ownerOf(defender[0]);
            }
            else {
                return address(0);
            }
        }
        else {
            if (faunaArmy.minionExists(defender[0])) {
                return faunaArmy.ownerOf(defender[0]);
            }
            else {
                return address(0);
            }
        }
    }

    /**
     * @notice Get the field info
     * @param fieldID ID of the field
     * @return fieldInfo Leader, defender and side
    */ 
    function getFieldInfo(uint fieldID) public view returns (FieldInfo memory fieldInfo) {
        fieldInfo.leader = getFieldLeader(fieldID);
        if (fieldInfo.leader != address(0)) {
            fieldInfo.defender = getFieldDefender(fieldID);
            fieldInfo.isFlora = isFloraField[fieldID];
        }
        else {
            delete fieldInfo;
        }
    }

    /**
     * @notice Get field infos given range 
     * @return rangeInfo Info of every field
    */ 
    function getRangeInfo(uint startId, uint area) external view returns (FieldInfo[] memory rangeInfo) {
        rangeInfo = new FieldInfo[](area);
        for (uint i = 0; i < area; i++) {
            rangeInfo[i] = getFieldInfo(startId+i);
        }
    }

    /**
     * @notice Send flora army to conquer certain field
     * @param fieldID ID of the field
     * @param attackerID ID of the flora attacker minion
    */ 
    function floraConquer(uint fieldID, uint attackerID) external preCheck(fieldID) {
        require(
            !floraOnField[attackerID],
            "Battlefield: the flora minion already on field");
        require(
            floraArmy.ownerOf(attackerID) == msg.sender,
            "Battlefield: not the commander of the flora minion");
        uint[] memory defender = fieldDefender[fieldID];
        if (defender.length > 0) {
            uint defenderID = defender[0];
            if (isFloraField[fieldID]) {
                _fight(floraArmy, attackerID, floraArmy, defenderID);
                floraOnField[defenderID] = false;
            }
            else {
                _fight(floraArmy, attackerID, faunaArmy, defenderID);
                faunaOnField[defenderID] = false;
                faunaFieldCount--;
                floraFieldCount++;
            }
            fieldDefender[fieldID][0] = attackerID;
        }
        else {
            fieldDefender[fieldID].push(attackerID);
            floraFieldCount++;
        }
        floraOnField[attackerID] = true;
        isFloraField[fieldID] = true;

        emit FieldState(fieldID, msg.sender, true, fieldDefender[fieldID]);
    }

    /**
     * @notice Send fauna army to conquer certain field
     * @param fieldID ID of the field
     * @param attackerID ID of the fauna attacker minion
    */ 
    function faunaConquer(uint fieldID, uint attackerID) external preCheck(fieldID) {
        require(
            !faunaOnField[attackerID],
            "Battlefield: the fauna minion already on field");
        require(
            faunaArmy.ownerOf(attackerID) == msg.sender,
            "Battlefield: not the commander of the fauna minion");
        uint[] memory defender = fieldDefender[fieldID];
        if (defender.length > 0) {
            uint defenderID = defender[0];
            if (isFloraField[fieldID]) {
                _fight(faunaArmy, attackerID, floraArmy, defenderID);
                floraOnField[defenderID] = false;
                floraFieldCount--;
                faunaFieldCount++;
            }
            else {
                _fight(faunaArmy, attackerID, faunaArmy, defenderID);
                faunaOnField[defenderID] = false;
            } 
            fieldDefender[fieldID][0] = attackerID;
        }
        else {
            fieldDefender[fieldID].push(attackerID);
            faunaFieldCount++;            
        }
        faunaOnField[attackerID] = true;
        isFloraField[fieldID] = false;

        emit FieldState(fieldID, msg.sender, false, fieldDefender[fieldID]);
    }

    /**
     * @notice Retreat from certain field
     * @param fieldID ID of the field
    */    
    function retreat(uint fieldID) external {
        uint[] memory defender = fieldDefender[fieldID];
        require(
            defender.length > 0,
            "Battlefield: retreat from empty field");
        uint defenderID = defender[0];
        if (isFloraField[fieldID]) {
            require(
                floraArmy.ownerOf(defenderID) == msg.sender,
                "Battlefield: not leader");
            floraOnField[defenderID] = false;
        }
        else {
            require(
                faunaArmy.ownerOf(defenderID) == msg.sender,
                "Battlefield: not leader");
            faunaOnField[defenderID] = false;
        }
        delete fieldDefender[fieldID];

        emit FieldState(fieldID, address(0), isFloraField[fieldID], fieldDefender[fieldID]);
    }

    /**
     * @dev Check if locked or out of range
     * @param fieldID ID of the field
    */
    modifier preCheck(uint fieldID) {
        require(
            !fieldLocked,
            "Battlefield: battlefield is locked now");
        require(
            fieldID < totalArea,
            "Battlefield: field out of range");
        _;
    }

    /**
     * @dev Determine win or not, cancel tx if lose
     * @param attackerSide Which side of attacker army
     * @param attackerID ID of attacker minion
     * @param defenderSide Which side of defender army
     * @param defenderID ID of defender minion
    */
    function _fight(ARMY attackerSide, uint attackerID,
                    ARMY defenderSide, uint defenderID
                    ) private view {
        if (defenderSide.minionExists(defenderID)) {
            (,bool aArmed,,int aPower) = attackerSide.getMinionInfo(attackerID);
            (,bool dArmed,,int dPower) = defenderSide.getMinionInfo(defenderID);
            require(aArmed, "Battlefield: attacker should be armed");
            require(!dArmed || aPower > dPower, "Battlefield: defeated");
        }
    }
}","keccak256":"0xc170910147c5cc9ed683a351f6416c7ed6f4acfcc1382e445c474bd6382a805f","license":"MIT"},"contracts/Battlefield.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "./BattleBase.sol";

/**
 * @title Battlefield of fighting for next generation
 * @notice Define voting system on battlefield
 * @author Justa Liang
 */
contract Battlefield is BattleBase, ERC721URIStorage {

    /// @dev Proposal contents
    struct Proposal {
        address proposer;
        string prefixURI;
        uint voteCount;
    }

    /// @notice All the proposals
    Proposal[] public proposals;

    /// @notice Latest generation in which field has voted
    mapping (uint => uint) public fieldGeneration;

    /// @notice Latest time updated for proposal or vote
    uint public updateTime;

    /// @notice Time interval of proposal state
    uint public propInterval;

    /// @notice Time interval of vote state
    uint public voteInterval;

    /// @notice One generation means going through proposal and vote
    uint public generation;

    /// @notice Name of assembly metadata of the medal designs 
    string public seriesName;

    /// @notice Slotting fee for making a proposal
    uint public slottingFee;

    /// @notice Emit when someone propose
    event Propose(
        address indexed proposer,
        uint proposalID,
        string prefixURI
    );

    /// @notice Emit when someone vote behalf of field
    event Vote(
        uint indexed fieldID,
        address indexed voter,
        uint indexed proposalID,
        uint voteCount
    );

    /// @notice Emit the winning proposal's info
    event Winner(
        uint indexed generation,
        address indexed winner,
        string tokenURI,
        uint proposalCount,
        uint voteCount,
        uint totalArea,
        bool floraWin,
        bool faunaWin
    );

    /**
     * @dev Set addresses of interactive contracts
     * @param floraArmyAddr Address of FloraArmy contract
     * @param faunaArmyAddr Address of FaunaArmy contract
    */
    constructor(address floraArmyAddr, address faunaArmyAddr)
        BattleBase(floraArmyAddr, faunaArmyAddr)
        ERC721("Flora&Fauna Battlefield", "F&F-BTF")
    {
        generation = 1;
        propInterval = 30 days;
        voteInterval = 5 days;
        seriesName = "series.json";
        slottingFee = 1e12 wei;
        updateTime = block.timestamp;
    }

    /**
     * @notice Get number of current proposals
     * @return Number of current proposals
    */
    function getProposalCount() external view returns (uint) {
        return proposals.length;
    }

    /**
     * @notice Get details of all proposals
     * @return Details of all proposals
    */
    function getAllProposalInfo() external view
                returns (Proposal[] memory) {
        return proposals;
    }

    /**
     * @notice Propose for new style of medals
     * @param prefixURI Prefix of the URI
    */
    function propose(string calldata prefixURI) payable external propState {
        require(
            msg.value >= slottingFee,
            "Battlefield: not enough slotting fee");
        proposals.push(Proposal(msg.sender, prefixURI, 0));

        emit Propose(msg.sender, proposals.length-1, prefixURI);
    }

    /**
     * @notice Start the vote state
    */
    function startVote() external propState {
        require(
            proposals.length > 1,
            "Battlefield: not enough proposals");
        uint currentTime = block.timestamp;
        require(
            currentTime >= updateTime + propInterval,
            "Battlefield: not yet to start vote");
        updateTime = currentTime;
        fieldLocked = true;
    }

    /**
     * @notice Vote behalf of certain field
     * @param fieldID ID of the field
     * @param proposalID ID of the proposal
    */
    function vote(uint fieldID, uint proposalID) external voteState {
        require(
            fieldGeneration[fieldID] < generation,
            "Battlefield: field has voted in this generation");
        uint[] memory defender = fieldDefender[fieldID];
        require(
            defender.length > 0,
            "Battlefield: empty field can't vote");
        if (isFloraField[fieldID]) {
            require(
                floraArmy.ownerOf(defender[0]) == msg.sender,
                "Battlefield: not leader");
            require(
                floraFieldCount >= faunaFieldCount,
                "Battlefield: you're loser side");
        }
        else {
            require(
                faunaArmy.ownerOf(defender[0]) == msg.sender,
                "Battlefield: not leader");
            require(
                faunaFieldCount >= floraFieldCount,
                "Battlefield: you're loser side");

        }
        Proposal storage target = proposals[proposalID];
        target.voteCount++;
        fieldGeneration[fieldID] = generation;

        emit Vote(fieldID, msg.sender, proposalID, target.voteCount);
    }

    /**
     * @notice End the vote state, change medal styles and mint an assembly metadata to winner
    */
    function endVote() external voteState {
        uint currentTime = block.timestamp;
        require(
            currentTime >= updateTime + voteInterval,
            "Battlefield: not yet to end vote");
        updateTime = currentTime;
        
        uint maxVote = 0;
        uint maxIdx = 0;
        for (uint i = 0; i < proposals.length; i++) {
            if (proposals[i].voteCount > maxVote) {
                maxVote = proposals[i].voteCount;
                maxIdx = i;
            }
        }
        
        Proposal memory winning = proposals[maxIdx];

        bool floraWin = false;
        bool faunaWin = false;
        if (floraFieldCount >= faunaFieldCount) {
            floraArmy.updateBaseURI(winning.prefixURI);
            floraWin = true;
        }
        if (faunaFieldCount >= floraFieldCount) {
            faunaArmy.updateBaseURI(winning.prefixURI);
            faunaWin = true;
        }

        _safeMint(winning.proposer, generation);
        _setTokenURI(generation, string(abi.encodePacked(winning.prefixURI, seriesName)));

        delete proposals;
        fieldLocked = false;

        emit Winner(generation,
                    winning.proposer,
                    tokenURI(generation),
                    proposals.length,
                    winning.voteCount,
                    totalArea,
                    floraWin,
                    faunaWin);
        generation++;
    }

    /// @dev Check if under vote state
    modifier voteState() {
        require(fieldLocked, "Battlefield: not in proposal state");
        _;
    }

    /// @dev Check if under proposal state
    modifier propState() {
        require(!fieldLocked, "Battlefield: not in vote state");
        _;
    }

    /**
     * @dev Claim the funds from slotting fee
     * @param amount Amount of Ether
     * @param receiver Address of receiver
    */    
    function claimFunds(uint amount, address payable receiver) external onlyOwner {
        receiver.transfer(amount);
    } 

    function changePropInterval(uint propInterval_) external onlyOwner {
        propInterval = propInterval_;
    }

    function changeVoteInterval(uint voteInterval_) external onlyOwner {
        voteInterval = voteInterval_;
    }

    function changeSlottingFee(uint slottingFee_) external onlyOwner {
        slottingFee = slottingFee_;
    }

    function changeSeriesName(string calldata seriesName_) external onlyOwner {
        seriesName = seriesName_;
    }
}","keccak256":"0x8d3ed258ec7b619d1aa869eef73ddfbecb8dc3e4c4043eec2f4ad7f886e9d5be","license":"MIT"}},"version":1}
