pragma solidity ^0.8.0;

import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "./ArmyEnhancer.sol";

/**
 * @notice ENS registry to get chainlink resolver
 */
interface ENS {
    function resolver(bytes32) external view returns (Resolver);
}

/**
 * @notice Chainlink resolver to get price feed proxy
 */
interface Resolver {
    function addr(bytes32) external view returns (address);
}

/**
 * @title Army contract with basic command
 * @author Justa Liang
 */
abstract contract ArmyBase is ERC721Enumerable {

    /// @notice Corresponding Enhancer contract
    ENHR public enhancerContract;

    /// @notice Serial number of minions, imply how many minions have been created
    uint public serialNumber;

    /// @notice Inital minion's power
    int public initPower;

    /// @notice ENS interface (fixed address)
    ENS public ens;

    /// @dev Minion data structure
    struct Minion {
        address     branchAddr;   // branch address (which proxy of Chainlink price feed)
        bool        armed;        // armed or not
        int         envFactor;    // environment factor (latest updated price from Chainlink)
        int         power;        // power of the minion
    }

    /// @dev Minion profile to view
    struct MinionProfile {
        address     branch;
        bool        armed;
        int         price;
        int         power;
        string      uri;
    }

    /// @dev Minion data storage
    mapping(uint => Minion) public minions;

    /// @notice Emit when minion's state changes 
    event MinionState(
        uint indexed minionID,
        address indexed branchAddress,
        bool indexed armed,
        int environmentFactor,
        int power
    );

    /**
     * @dev Set name, symbol, and addresses of interactive contracts
     * @param ensRegistryAddr Address of ENS Registry
    */
    constructor(address ensRegistryAddr) {
        serialNumber = 0;
        initPower = 1000;
        ens = ENS(ensRegistryAddr);
    }

    /**
     * @notice Get if minion exists
     * @param minionID ID of the minion
     * @return Exists or not
    */
    function minionExists(uint minionID) external view returns (bool) {
        return _exists(minionID);
    }

    /**
     * @notice Get minion's on-chain information
     * @param minionID ID of the minion
     * @return On-chain information of the minion
    */
    function getMinionInfo(uint minionID) public view returns (address, bool, int, int) {
        require(
            _exists(minionID),
            "ARMY: commander query for nonexistent minion");
        Minion storage m = minions[minionID];
        return (m.branchAddr, m.armed, m.envFactor, m.power);
    }

    /**
     * @notice Get minion IDs, like (2,6,9), given commander
     * @param commander Commander of these minions
     * @return minionIDs IDs of these minions
    */
    function getMinionIDs(address commander) public view returns(uint[] memory minionIDs) {
        uint minionCount = balanceOf(commander);
        minionIDs = new uint[](minionCount);
        for (uint i = 0; i < minionCount; i++) {
            minionIDs[i] = tokenOfOwnerByIndex(commander, i);
        }
    }

    /**
     * @notice Recruit a minion
     * @param branchHash ENS-namehash of given pair (ex: eth-usd.data.eth)
     * @return ID of the newly recruited minion
    */
    function recruit(bytes32 branchHash) external returns (uint) {
        address branchAddr = _resolve(branchHash);
        require(
            branchAddr != address(0),
            "ARMY: invalid branch");

        // get current price
        AggregatorV3Interface pricefeed = AggregatorV3Interface(branchAddr);
        (,int currPrice,,,) = pricefeed.latestRoundData();

        // mint minion and store its data on chain
        uint newID = serialNumber;
        _safeMint(msg.sender, newID);
        minions[newID] = Minion(branchAddr, false, currPrice, initPower);

        emit MinionState(newID, branchAddr, false, currPrice, initPower);
        serialNumber++;
        return newID;
    }

    /**
     * @dev Check if commander can command the minion
     * @param minionID ID of the minion
    */
    modifier checkCommander(uint minionID) {
        require(
            _isApprovedOrOwner(msg.sender, minionID),
            "ARMY: commander can't command the minion");
        _;
    }

    /**
     * @dev Resolve ENS-namehash to Chainlink price feed proxy
     * @param node ENS-namehash of given pair
     * @return Chainlink price feed proxy
    */
    function _resolve(bytes32 node) internal view returns (address) {
        Resolver resolver = ens.resolver(node);
        return resolver.addr(node);
    }
}","keccak256":"0x5c2d8e506a64b6a37fcbbbb7b6c0c3fb90ecb132ef090efd73f179b99344b07b","license":"MIT"},"contracts/ArmyEnhancer.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @notice Unique operations only for Army contract
 */
interface ENHR {
    function produce(address, uint) external;
    function consume(address, uint) external;
}

/**
 * @title Organic Enhancer
 * @notice ERC20 token minted or burnt by Army contract
 * @author Justa Liang
 */
contract ArmyEnhancer is ERC20, Ownable {

    /**
     * @dev ERC20 constructor
     * @param name Token name
     * @param symbol Token symbol
    */
    constructor(string memory name, string memory symbol) 
        ERC20(name, symbol) {
    }

    /**
     * @dev Produce enhancer for commander
     * @param commander Player of Army
     * @param amount Amount of enhancer (no decimal concerned)
    */
    function produce(address commander, uint amount) external onlyOwner {
        _mint(commander, amount*10**decimals());
    }

    /**
     * @dev Consume enhancer from commander
     * @param commander Player of Army
     * @param amount Amount of enhancer (no decimal concerned)
    */
    function consume(address commander, uint amount) external onlyOwner {
        _burn(commander, amount*10**decimals());
    }
}","keccak256":"0x196859750546c6de7749bdf8933b0ba27dbf0a8c61a1d9856453e19c362ac963","license":"MIT"},"contracts/ArmyRank.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./ArmyBase.sol";

/**
 * @title Ranking system of FloraArmy and FaunaArmy
 * @author Justa Liang
 */
abstract contract ArmyRank is ArmyBase, Ownable {

    /// @notice Base URI (prefix)
    string public baseURI;

    /// @notice Level of power to reach to upgrade minions
    int[5] public powerLevels;

    /// @notice Metadata filenames
    string[5] public metadataNames;

    // Mapping for token URIs
    mapping(uint => string) private _tokenURIs;

    /**
     * @dev Set power levels and metadata filenames
     * @param powerLevels_ Array with length 5, from high level to low
     * @param metadataNames_ Array with length 5, from high level URI to low
    */
    constructor(string memory baseURI_,
                int[5] memory powerLevels_,
                string[5] memory metadataNames_) {
        for (uint i = 0; i < 5; i++) {
            powerLevels[i] = powerLevels_[i];
            metadataNames[i] = metadataNames_[i];
        }
        baseURI = baseURI_;
    }

    /**
     * @notice Get minion's metadata URI
     * @param minionID ID of the minion
    */
    function tokenURI(uint minionID) public view override returns (string memory) {
        require(
            _exists(minionID),
            "ARMY: commander query for nonexistent minion");
    
        string memory grantedURI = _tokenURIs[minionID];
        if (bytes(grantedURI).length > 0) {
            return grantedURI;
        }
        else {
            (,,,int power) = getMinionInfo(minionID);
            for (uint i = 0; i < powerLevels.length; i++) {
                if (power >= powerLevels[i]) {
                    return string(abi.encodePacked(baseURI, metadataNames[i]));
                }
            }
            return string(abi.encodePacked(baseURI, metadataNames[4]));
        }
    }

    /**
     * @notice Grant minion with current token URI
     * @param minionID ID of the minion
    */
    function grant(uint minionID) external checkCommander(minionID) {
        string memory currentURI;
        (,,,int power) = getMinionInfo(minionID);
        for (uint i = 0; i < powerLevels.length; i++) {
            if (power >= powerLevels[i]) {
                currentURI = string(abi.encodePacked(baseURI, metadataNames[i]));
            }
        }
        _tokenURIs[minionID] = currentURI;
    }

    /**
     * @notice Get minion's profile
     * @param minionID ID of the minion
     * @return profile Minion info and tokeURI
    */    
    function getMinionProfile(uint minionID) public view
            returns (MinionProfile memory profile) {
            require(
                _exists(minionID),
                "ARMY: commander query for nonexistent minion");            
            Minion storage m = minions[minionID];
            profile.branch = m.branchAddr;
            profile.armed = m.armed;
            profile.price = m.envFactor;
            profile.power = m.power;
            profile.uri = tokenURI(minionID);
    }

    /**
     * @notice Get all minions' info given minion IDs
     * @param minionIDs IDs of the minions
     * @return teamProfile Array of minion info
    */
    function getTeamProfile(uint[] calldata minionIDs)
            external view returns (MinionProfile[] memory teamProfile) {
        teamProfile = new MinionProfile[](minionIDs.length);
        for (uint i = 0; i < minionIDs.length; i++) {
            teamProfile[i] = getMinionProfile(minionIDs[i]);
        }
    }

    /**
     * @notice Liberate a minion and get some enhancer
     * @param minionID ID of the minion
    */
    function liberate(uint minionID) external checkCommander(minionID) {
        Minion storage target = minions[minionID];
        if (target.power > initPower) {
             enhancerContract.produce(msg.sender, uint(target.power - initPower));
        }
        _burn(minionID);
        delete minions[minionID];
        if (bytes(_tokenURIs[minionID]).length != 0) {
            delete _tokenURIs[minionID];
        }
    }

    /**
     * @dev Update branch prefix (give ownership to Battlefield contract in the future)
     * @param baseURI_ Prefix of URI to be set
    */
    function updateBaseURI(string calldata baseURI_) external onlyOwner {
        baseURI = baseURI_;
    }

    /**
     * @dev Change power levels (will be discarded)
     * @param powerLevels_ Array with length 5, from low level to high
    */
    function changePowerLevels(int[5] calldata powerLevels_) external onlyOwner {
        for (uint i = 0; i < 5; i++) {
            powerLevels[i] = powerLevels_[i];
        }
    }

    /**
     * @dev Change metadat filename (will be discarded)
     * @param metadataNames_ Array with length 5, from low level URI to high
    */
    function changeMetadataNames(string[5] calldata metadataNames_) external onlyOwner {
        for (uint i = 0; i < 5; i++) {
            metadataNames[i] = metadataNames_[i];
        }
    }
}","keccak256":"0xb82844a5cf3bc276d219acdbad06f3b00d0e860c258a97c2503ae5e61a399af6","license":"MIT"},"contracts/FaunaArmy.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./ArmyRank.sol";

/**
 * @title Fauna Army, which grows in bearish market
 * @notice ERC721 token cultivated by predicting market price (using Chainlink oracle)
 * @author Justa Liang
 */
contract FaunaArmy is ArmyRank {

    /**
     * @dev Set name, symbol, and addresses of interactive contracts
     * @param ensRegistryAddr Address of ENS Registry
    */
    constructor(address ensRegistryAddr, uint initEnhancer,
                string memory baseURI, int[5] memory powerLevels, string[5] memory metadataNames) 
        ERC721("FaunaArmy", "FaunA")
        ArmyBase(ensRegistryAddr)
        ArmyRank(baseURI, powerLevels, metadataNames)
    {
        enhancerContract = ENHR(address(new ArmyEnhancer("Hemoglobin", "HGB")));
        enhancerContract.produce(msg.sender, initEnhancer);
    }

    /**
     * @notice Train a minion and update the environment factor
     * @param minionID ID of the minion
    */
    function train(uint minionID) external checkCommander(minionID) {
        Minion storage target = minions[minionID];
        require(
            target.armed,
            "ARMY: minion is already in training state");

        // get current price
        AggregatorV3Interface pricefeed = AggregatorV3Interface(target.branchAddr);
        (,int currPrice,,,) = pricefeed.latestRoundData();

        // update on-chain data
        target.envFactor = currPrice;
        target.armed = false;

        // emit minion state
        emit MinionState(minionID, target.branchAddr, false, currPrice, target.power);
    }

    /**
     * @notice Arm a minion and update its power
     * @param minionID ID of the minion
    */
    function arm(uint minionID) external checkCommander(minionID) {
        Minion storage target = minions[minionID];
        require(
            !target.armed,
            "ARMY: minion is already armed");

        // get current price
        AggregatorV3Interface pricefeed = AggregatorV3Interface(target.branchAddr);
        (,int currPrice,,,) = pricefeed.latestRoundData();

        // update on-chain data
        target.power = ((target.envFactor << 16)/currPrice*target.power) >> 16;
        target.envFactor = currPrice;
        target.armed = true;

        // emit minion state
        emit MinionState(minionID, target.branchAddr, true, currPrice, target.power);
    }

    /**
     * @notice Use Hemoglobin to stimulate an armed minion to catch up with training
     * @dev Commander cost Hemoglobin
     * @param minionID ID of the minion
    */
    function boost(uint minionID) external checkCommander(minionID) {
        Minion storage target = minions[minionID];
        require(
            target.armed,
            "ARMY: minion is already in training state");

        // get current price
        AggregatorV3Interface pricefeed = AggregatorV3Interface(target.branchAddr);
        (,int currPrice,,,) = pricefeed.latestRoundData();

        // change state
        if (currPrice < target.envFactor) {
             enhancerContract.consume(msg.sender, uint(((target.envFactor << 16)/currPrice*target.power) >> 16));
        }
        target.armed = false;

        // emit minion state
        emit MinionState(minionID, target.branchAddr, false, target.envFactor, target.power);
    }

    /**
     * @notice Use Hemoglobin to heal a minion who suffer from negative training
     * @dev Commander cost Hemoglobin
     * @param minionID ID of the minion
    */
    function heal(uint minionID) external checkCommander(minionID) {
        Minion storage target = minions[minionID];
        require(
            !target.armed,
            "ARMY: minion is not in training state");

        // get current price
        AggregatorV3Interface pricefeed = AggregatorV3Interface(target.branchAddr);
        (,int currPrice,,,) = pricefeed.latestRoundData();

        // change state
        if (currPrice > target.envFactor) {
            enhancerContract.consume(msg.sender, uint(((currPrice << 16)/target.envFactor*target.power) >> 16));
        }
        target.armed = true;

        // emit minion state
        emit MinionState(minionID, target.branchAddr, true, target.envFactor, target.power);
    }
}","keccak256":"0xa9d5f22a07e38dc818978b6f8e2651f5f4b64b3a42ded0495e99fe310fbcf24a","license":"MIT"}},"version":1}
